import React, {Component } from 'react';
import './App.css';
import DogBar from './DogBar';

class App extends Component {

  state = {
    dogs: [],
    clicked: 'none'
  }

  componentDidMount () {
    fetch('http://localhost:3000/pups').then(response => response.json()).then(dogs => this.setState({
      dogs: dogs
    }))
  }

  changeTheName = (dog) => {
    console.log(dog)
    this.setState({
      clicked: dog.name
    })

  }


  render () {
    console.log(this.state.clicked)
      return (
          <div className="App">
            <div id="filter-div">
              <button id="good-dog-filter">Filter good dogs: OFF</button>
            </div>
            <div id="dog-bar">
              <DogBar dogs={this.state.dogs} changeTheName={this.changeTheName}/>
            </div>
            <div id="dog-summary-container">
              <h1>DOGGO:</h1>
              <div id="dog-info">
                  {this.state.clicked}
              </div>
            </div>
          </div>
      )
  }
}

export default App;
